for(var app=new Vue({el:"#app",data:{commentBoxFlag:!0},methods:{rand:function(a,n){return parseInt(Math.random()*(n-a)+1)+a},barrage:function(a){$(".danmu").css("animation-play-state","paused");for(var n=0;n<a.length;n++){var t=Math.floor(400*Math.random()+1),r='<div class="fr"><span style="margin-right: -'+t+'px;" class="ts3dright">'+a[n]+"</span></div>";$(".barrage").append(r),$(".barrage div").addClass("danmu")}setTimeout(function(){$(".danmu").css("animation-play-state","running")},1e3)},discuss:function(){var a=$("#comment-box"),n=$("#comment"),t=a.outerWidth();app.commentBoxFlag?(a.show(),a.animate({right:0}),n.animate({right:200})):(n.animate({right:0}),a.animate({right:-t},function(){a.hide()})),app.commentBoxFlag=!app.commentBoxFlag}}}),array=[],i=0;i<20;i++)array[i]="傻逼年年有！";app.barrage(array);